header.script:
//
(function() {
    const btn = document.getElementById('mobile-menu-btn');
    const overlay = document.getElementById('mobile-menu-overlay');
    const backdrop = document.getElementById('mobile-menu-backdrop');
    const drawer = document.getElementById('mobile-menu-drawer');
    const closeBtn = document.getElementById('close-menu-btn');
    const desktopList = document.getElementById('desktop-menu-list');
    const mobileList = document.getElementById('mobile-menu-items');

    // نسخ الروابط من القائمة العلوية إلى قائمة الجوال
    if(desktopList && mobileList && mobileList.children.length === 0) {
        const links = desktopList.querySelectorAll('a');
        links.forEach(link => {
            const li = document.createElement('li');
            const a = document.createElement('a');
            a.href = link.href;
            a.textContent = link.textContent;
            a.className = 'block px-6 py-3 text-slate-700 hover:bg-indigo-50 hover:text-indigo-600 font-medium transition-colors border-r-2 border-transparent hover:border-indigo-600';
            li.appendChild(a);
            mobileList.appendChild(li);
        });
    }

    function openMenu() {
        overlay.classList.remove('hidden');
        // setTimeout للسماح بالانتقال (transition)
        setTimeout(() => {
            backdrop.classList.remove('opacity-0');
            drawer.classList.remove('translate-x-full');
        }, 10);
    }

    function closeMenu() {
        backdrop.classList.add('opacity-0');
        drawer.classList.add('translate-x-full');
        setTimeout(() => {
            overlay.classList.add('hidden');
        }, 300);
    }

    if(btn) btn.addEventListener('click', openMenu);
    if(closeBtn) closeBtn.addEventListener('click', closeMenu);
    if(backdrop) backdrop.addEventListener('click', closeMenu);
})();
//

body.script:
//
// Cookie Banner Logic
document.addEventListener('DOMContentLoaded', () => {
    const banner = document.getElementById('cookieBanner');
    const acceptBtn = document.getElementById('acceptCookies');
    const closeBtn = document.getElementById('closeCookieBanner');

    // Check if user already accepted
    if (!localStorage.getItem('cookiesAccepted')) {
        // Show banner after a small delay
        setTimeout(() => {
            banner.classList.remove('translate-y-full');
        }, 2000);
    }

    const hideBanner = () => {
        banner.classList.add('translate-y-full');
    };

    acceptBtn.addEventListener('click', () => {
        localStorage.setItem('cookiesAccepted', 'true');
        hideBanner();
    });

    closeBtn.addEventListener('click', hideBanner);
});

const API_BASE_URL = "https://pdfaraby.onrender.com";

// === Tool Configuration ===
const TOOLS = {
    'HOME': { id: 'tool-pdf-to-word', endpoint: '/convert', method: 'POST', type: 'pdf', multiple: false, title: 'PDF to Word' },
    'PDF_TO_IMAGE': { id: 'tool-pdf-to-image', endpoint: '/convert/to-image', method: 'POST', type: 'pdf', multiple: false, title: 'PDF to Image' },
    'IMAGE_TO_PDF': { id: 'tool-image-to-pdf', endpoint: '/convert/from-image', method: 'POST', type: 'image', multiple: true, title: 'Image to PDF' },
    'MERGE_PDF': { id: 'tool-merge-pdf', endpoint: '/merge', method: 'POST', type: 'pdf', multiple: true, title: 'Merge PDF' },
    'COMPRESS_PDF': { id: 'tool-compress-pdf', endpoint: '/compress', method: 'POST', type: 'pdf', multiple: false, title: 'Compress PDF' }
};

// Determine current tool based on URL
let currentToolKey = 'HOME';
const path = window.location.pathname;

// Check for specific generated Blogger pages
if (path.includes('/p/pdf.html')) currentToolKey = 'PDF_TO_IMAGE';
else if (path.includes('/p/pdf_01520063794.html')) currentToolKey = 'IMAGE_TO_PDF';
else if (path.includes('/p/pdf_77.html')) currentToolKey = 'MERGE_PDF';
else if (path.includes('/p/pdf_29.html')) currentToolKey = 'COMPRESS_PDF';

const currentTool = TOOLS[currentToolKey];

// Show active tool UI
document.querySelectorAll('.tool-content').forEach(el => el.classList.add('hidden'));
document.getElementById(currentTool.id).classList.remove('hidden');

// === Drag & Drop Logic ===
let selectedFiles = [];
const MAX_FILE_SIZE = 30 * 1024 * 1024;

const dropZone = document.getElementById('dropZone');
const fileInput = document.getElementById('fileInput');
const fileInfo = document.getElementById('fileInfo');
const removeFile = document.getElementById('removeFile');
const convertBtn = document.getElementById('convertBtn');
const uploadSection = document.getElementById('uploadSection');
const convertingSection = document.getElementById('convertingSection');
const successSection = document.getElementById('successSection');
const errorSection = document.getElementById('errorSection');
const progressBar = document.getElementById('progressBar');
const progressText = document.getElementById('progressText');
const errorMessage = document.getElementById('errorMessage');
const downloadBtn = document.getElementById('downloadBtn');
const convertAnotherBtn = document.getElementById('convertAnotherBtn');
const tryAgainBtn = document.getElementById('tryAgainBtn');

// Update Input Attributes based on tool
if (currentTool.multiple) fileInput.setAttribute('multiple', 'true');
fileInput.setAttribute('accept', currentTool.type === 'pdf' ? '.pdf' : '.jpg,.jpeg,.png');

// Dynamic Text Updates
document.querySelector('#dropZone h3').textContent = currentTool.type === 'pdf' ? 
    (currentTool.multiple ? 'اسحب ملفات PDF هنا' : 'اسحب ملف PDF هنا') : 
    'اسحب الصور هنا';

['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
    dropZone.addEventListener(eventName, e => { e.preventDefault(); e.stopPropagation(); }, false);
});

['dragenter', 'dragover'].forEach(eventName => {
    dropZone.addEventListener(eventName, () => dropZone.classList.add('dragover'), false);
});

['dragleave', 'drop'].forEach(eventName => {
    dropZone.addEventListener(eventName, () => dropZone.classList.remove('dragover'), false);
});

dropZone.addEventListener('drop', e => handleFiles(e.dataTransfer.files), false);
fileInput.addEventListener('change', e => handleFiles(e.target.files));

function handleFiles(files) {
    if (files.length === 0) return;

    // Validation
    const validFiles = Array.from(files).filter(file => {
        if (currentTool.type === 'pdf' && !file.type.includes('pdf')) return false;
        if (currentTool.type === 'image' && !file.type.includes('image')) return false;
        return true;
    });

    if (validFiles.length === 0) { showError('نوع الملف غير مدعوم لهذه الأداة.'); return; }

    // Check Size
    const totalSize = validFiles.reduce((acc, file) => acc + file.size, 0);
    if (totalSize > MAX_FILE_SIZE) { showError('حجم الملفات كبير جداً (الحد الأقصى 30 ميجابايت).'); return; }

    selectedFiles = currentTool.multiple ? validFiles : [validFiles[0]];

    // Update UI
    document.getElementById('fileName').textContent = selectedFiles.length > 1 ? 
        `${selectedFiles.length} ملفات مختارة` : selectedFiles[0].name;
    document.getElementById('fileSize').textContent = (totalSize / (1024 * 1024)).toFixed(2) + ' MB';

    fileInfo.classList.remove('hidden');
    convertBtn.classList.remove('hidden');
    dropZone.classList.add('hidden');
}

removeFile.addEventListener('click', () => {
    selectedFiles = []; fileInput.value = '';
    fileInfo.classList.add('hidden'); convertBtn.classList.add('hidden'); dropZone.classList.remove('hidden');
});

convertBtn.addEventListener('click', () => {
    if (selectedFiles.length === 0) return;
    uploadSection.classList.add('hidden');
    convertingSection.classList.remove('hidden');

    const formData = new FormData();
    selectedFiles.forEach(file => formData.append('file', file)); // API expects 'file' for both single/multiple usually, or adapt backend
    // Note: Backend ConvertFromImage/Merge expects 'file' as list. request.files.getlist('file') works if key is same.

    if (currentToolKey === 'HOME') formData.append('engine', 'standard');

    let progress = 0;
    const progressInterval = setInterval(() => {
        if (progress < 90) {
            progress += Math.random() * 5;
            const displayProgress = Math.min(Math.round(progress), 90);
            progressBar.style.width = displayProgress + '%';
            progressText.textContent = displayProgress + '%';
        }
    }, 300);

    fetch(`${API_BASE_URL}${currentTool.endpoint}`, { method: 'POST', body: formData })
    .then(res => res.json())
    .then(data => {
        clearInterval(progressInterval);
        if (data.success) {
            progressBar.style.width = '100%';
            progressText.textContent = '100%';
            setTimeout(() => {
                convertingSection.classList.add('hidden');
                successSection.classList.remove('hidden');
                downloadBtn.onclick = () => window.location.href = `${API_BASE_URL}${data.download_url}`;

                // Update download text based on file type
                const ext = data.filename.split('.').pop().toUpperCase();
                downloadBtn.innerHTML = `<i class="fas fa-download"></i> تحميل الملف (${ext})`;
            }, 600);
        } else { showError(data.error || 'حدث خطأ غير معروف'); }
    })
    .catch(err => {
        clearInterval(progressInterval);
        console.error(err);
        showError('خطأ في الاتصال بالسيرفر.');
    });
});

function showError(msg) {
    errorMessage.textContent = msg;
    uploadSection.classList.add('hidden');
    convertingSection.classList.add('hidden');
    successSection.classList.add('hidden');
    errorSection.classList.remove('hidden');
}

[convertAnotherBtn, tryAgainBtn].forEach(btn => {
    btn.addEventListener('click', () => {
        selectedFiles = []; fileInput.value = '';
        errorSection.classList.add('hidden'); successSection.classList.add('hidden'); convertingSection.classList.add('hidden');
        uploadSection.classList.remove('hidden'); fileInfo.classList.add('hidden'); convertBtn.classList.add('hidden'); dropZone.classList.remove('hidden');
        progressBar.style.width = '0%';
    });
});

// جلب عدد المستخدمين
fetch(`${API_BASE_URL}/stats`)
    .then(r => r.json())
    .then(data => {
        const count = data.user_count || 1250;
        document.getElementById('userCount').textContent = new Intl.NumberFormat('en-US').format(count);
    })
    .catch(() => document.getElementById('userCount').textContent = '+1,200');

document.getElementById('currentYear').textContent = new Date().getFullYear();
